mapper.select:
select, ui.nick_name, ui.sex, ui.image, ui.true_name, ui.id_number, ui.id_number_state
from t_user u,t_user_info ui where u.user_info_id = ui.id and u.del_flag=0 and u.account=#{account}
order by u.last_login_time DESC

select

, ui.nick_name, ui.sex, ui.image, ui.true_name, ui.id_number, ui.id_number_state
from t_user u,t_user_info ui where u.user_info_id = ui.id and u.del_flag=0


    and u.phone =#{account}


    and u.email =#{account}


    and u.account=#{account}

select

, ui.nick_name, ui.sex, ui.image, ui.true_name, ui.id_number, ui.id_number_state
from t_user u,t_user_info ui where u.user_info_id = ui.id and u.del_flag=0 and u.id = #{id}

select count(1)
from t_user u where 1=1


    and u.phone =#{account}


    and u.email =#{account}


    and u.account=#{account}

select, ui.nick_name, ui.sex, ui.image, ui.true_name, ui.id_number, ui.id_number_state
from t_user u,t_user_info ui where u.user_info_id = ui.id and u.del_flag=0 and u.email=#{email}
order by u.last_login_time DESC

select, ui.nick_name, ui.sex, ui.image, ui.true_name, ui.id_number, ui.id_number_state
from t_user u,t_user_info ui where u.user_info_id = ui.id and u.del_flag=0 and u.phone=#{phone}
order by u.last_login_time DESC

select, ui.nick_name, ui.sex, ui.image, ui.true_name, ui.id_number, ui.id_number_state
from t_user u,t_user_info ui where u.user_info_id = ui.id and u.del_flag=0 and u.subsidiary_user_id=#{subUserId}
and u.subsidiary_code=#{subCode} limit 1

        SELECT
        COUNT(1)
        FROM t_user u LEFT JOIN t_user_info ui ON u.user_info_id = ui.id
        WHERE u.del_flag=0

            AND u.account like '%${account}%'


            AND ui.nick_name like '%${nickName}%'


            AND u.email like '%${email}%'


            AND u.phone like '%${phone}%'


            AND u.subsidiary_user_id=#{subsidiaryUserId}


            AND u.subsidiary_code=#{subsidiaryCode}


            AND ui.sex=#{sex}



			AND u.last_login_time > #{startLastLoginTime}




			AND u.last_login_time < #{endLastLoginTime}




			AND u.reg_time > #{startRegTime}




			AND u.reg_time < #{endRegTime}

SELECT u.id, u.user_info_id, u.account, u.email, u.phone, u.pwd, u.salt, u.encrypt_type, u.subsidiary_user_id, u.reg_time, u.last_login_time, u.del_flag, u.create_time, u.create_uid, u.update_time, u.update_uid, u.subsidiary_code, ui.nick_name, ui.sex, ( CASE WHEN u.subsidiary_code='dj' THEN '乐盈电竞' WHEN u.subsidiary_code='yc' THEN '益彩网络' WHEN u.subsidiary_code='ybf' THEN '一比分' WHEN u.subsidiary_code='ht' THEN '华体星空' WHEN u.subsidiary_code='qysn' THEN '柒壹思诺' WHEN u.subsidiary_code='fy' THEN '法义网络' END ) subsidiaryName, ( CASE WHEN ui.sex=1 THEN '男' WHEN ui.sex=2 THEN '女' WHEN ui.sex=0 THEN '保密' END ) sexStr FROM t_user u LEFT JOIN t_user_info ui ON u.user_info_id = ui.id WHERE u.del_flag=0
AND u.account like '%${account}%'
AND ui.nick_name like '%${nickName}%'
AND u.email like '%${email}%'
AND u.phone like '%${phone}%'
AND u.subsidiary_user_id=#{subsidiaryUserId}
AND u.subsidiary_code=#{subsidiaryCode}
AND ui.sex=#{sex}
AND u.last_login_time > #{startLastLoginTime}
AND u.last_login_time < #{endLastLoginTime}
AND u.reg_time > #{startRegTime}
AND u.reg_time < #{endRegTime}
limit #{exportNum},#{exportSize}

mapper.update:
UPDATE t_user SET
last_login_time = #{lastLoginTime} ,
update_time = #{updateTime} ,
update_uid = #{updateUid}
WHERE
id = #{id}
